import { Node } from '../';
import { Mark } from '../enum';
export interface BoldNode extends Node {
    type: Mark.BOLD;
}
export interface StrongNode extends Node {
    type: Mark.STRONG;
}
export interface StrikeNode extends Node {
    type: Mark.STRIKE;
}
export interface UnderlineNode extends Node {
    type: Mark.UNDERLINE;
}
export interface ItalicNode extends Node {
    type: Mark.ITALIC;
}
export interface CodeNode extends Node {
    type: Mark.CODE;
}
export declare type LinkTarget = '_self' | '_blank' | null;
export declare type LinkType = 'url' | 'story' | 'asset';
export interface LinkAttributes {
    href: string;
    uuid: string | null;
    target: LinkTarget;
    linktype: LinkType;
}
export interface LinkNode extends Node {
    type: Mark.LINK;
    attrs: LinkAttributes;
}
export declare type MarkNodes = BoldNode | StrongNode | StrikeNode | UnderlineNode | ItalicNode | CodeNode | LinkNode;
